import React, { useState } from 'react';

const MasterForm = () => {
  const [state, setState] = useState({
    currentStep: 1,
    email: '',
    username: '',
    password: ''
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setState((prevState) => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const { email, username, password } = state;
    alert(`Your registration detail: \n 
      Email: ${email} \n 
      Username: ${username} \n
      Password: ${password}`);
  };

  const nextStep = () => {
    setState((prevState) => ({
      ...prevState,
      currentStep: prevState.currentStep >= 4 ? 3 : prevState.currentStep + 1
    }));
  };

  const prevStep = () => {
    setState((prevState) => ({
      ...prevState,
      currentStep: prevState.currentStep <= 1 ? 1 : prevState.currentStep - 1
    }));
  };

  const previousButton = state.currentStep !== 1 && (
    <button className="btn-secondary float-left" onClick={prevStep}>
      Previous
    </button>
  );

  const nextButton =
    state.currentStep < 5 && (
      <button className="btn-primary float-right" onClick={nextStep}>
        Next
      </button>
    );

  const submitButton =
    state.currentStep > 4 && (
      <button className="btn-primary float-right" type="submit">
        Submit
      </button>
    );

  return (
    <>
      <form onSubmit={handleSubmit}>
        <div className="card">
          <div className="card-header">Create an Account</div>
          <div className="card-body">
            <div className="card-title">
              <div id="progressBar" className="progress-bar">
                {[1, 2, 3, 4, 5].map((step) => (
                  <div
                    key={step}
                    className={`progress-step ${
                      step === state.currentStep ? 'active' : ''
                    }`}
                  ></div>
                ))}
              </div>
            </div>
            <div className="card-text"></div>

            {/* Step components will be placed here */}
          </div>
          <div className="card-footer">
            {previousButton}
            {nextButton}
            {submitButton}
          </div>
        </div>
      </form>
    </>
  );
};

export default Form;
