import SignUpInfo from "./signupinfo.jsx";
import PersonalInfo from "./personalinfo.jsx";
import FacultyDetails from "./facultydetails.jsx"; // Add this line
import OtherInfo from "./otherinfo.jsx";

function Forms() {
  const [page, setPage] = useState(0);
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    confirmPassword: "",
    firstName: "",
    lastName: "",
    username: "",
    nationality: "",
    other: "",
    // Add faculty-related fields here
    facultyName: "",
    facultyDepartment: "",
    facultyPosition: "",
  });

  const FormTitles = ["Sign Up", "Personal Info", "Faculty Details", "Other"]; // Add the new page title

  const PageDisplay = () => {
    switch (page) {
      case 0:
        return <SignUpInfo formData={formData} setFormData={setFormData} />;
      case 1:
        return <PersonalInfo formData={formData} setFormData={setFormData} />;
      case 2:
        return <FacultyDetails formData={formData} setFormData={setFormData} />; // Render the new page
      case 3:
        return <OtherInfo formData={formData} setFormData={setFormData} />;
      default:
        return null;
    }
  };

  return (
    <div className="form">
      <div className="progressbar">
        <div
          style={{ width: page === 0 ? "25%" : page === 1 ? "50%" : page === 2 ? "75%" : "100%" }}
        ></div>
      </div>
      <div className="form-container">
        <div className="header">
          <h1>{FormTitles[page]}</h1>
        </div>
        <div className="body">{PageDisplay()}</div>
        <div className="footer">
          <button
            disabled={page === 0}
            onClick={() => setPage((currPage) => currPage - 1)}
          >
            Prev
          </button>
          <button
            onClick={() => {
              if (page === FormTitles.length - 1) {
                alert("FORM SUBMITTED");
                console.log(formData);
              } else {
                setPage((currPage) => currPage + 1);
              }
            }}
          >
            {page === FormTitles.length - 1 ? "Submit" : "Next"}
          </button>
        </div>
      </div>
    </div>
  );
}

export default Forms;
