import React, { useState, useEffect } from 'react';
import {  useLocation, useNavigate } from 'react-router-dom';
import './vertical_navbar.css';
import HomeIcon from '@mui/icons-material/Home';
import BarChartIcon from '@mui/icons-material/BarChart';
import EditNoteIcon from '@mui/icons-material/EditNote';


const VerticalNavbar = () => {
  const [showMenu, setShowMenu] = useState(false);
  const [showMasterSubMenu, setShowMasterSubMenu] = useState(false);
  const [selectedField, setSelectedField] = useState(null);
  const location = useLocation();
  const navigate = useNavigate();

  const toggleMenu = () => {
    setShowMenu(!showMenu);
  };

  const toggleMasterSubMenu = () => {
    setShowMasterSubMenu(!showMasterSubMenu);
  };

  // const handleItemClick = (field) => {
  //   setSelectedField((prevSelected) => (prevSelected === field ? null : field));
  // };

  const handleMasterClick = () => {
    toggleMasterSubMenu();
  };

  const handleNavigate = (path) => {
    navigate(path);
    setSelectedField(null);
  };

  useEffect(() => {
   
    const currentPath = location.pathname.substring(1); 
    setSelectedField(currentPath || 'myevents');
  }, [location.pathname]);

  return (
    <div className="vertical-navbar">
      <div className="menu-icon" onClick={toggleMenu}>
        <FaBars />
      </div>
      <ul className={showMenu ? 'nav-links show' : 'nav-links'}>


        <li className={selectedField === 'myevents' ? 'selected' : ''} 
        onClick={() => handleNavigate('/myevents')}>
          <HomeIcon style={{ marginRight: '10px' }} />
          <b>My events </b>
        </li>

        {/* inventory_nav */}

        <li className={selectedField === 'eventrequest' ? 'selected' : ''}
        onClick={() => handleNavigate('/eventrequest')}>
          <BarChartIcon style={{ marginRight: '10px' }} />
          <b>Event request</b>
        </li>

        {/* enquiries_nav */}

        <li className={selectedField === 'reports' ? 'selected' : ''} 
        onClick={() => handleNavigate('/reports')}>
          <EditNoteIcon style={{ marginRight: '10px' }} />
          <b>Reports</b>
        </li>

       
      </ul>
    </div>
  );
};

export default VerticalNavbar;
