import React, { useState } from "react";
import VerticalNavbar from '../navbar/navbar.jsx';
import TopBar from '../topbar/topbar.jsx';
import Button from '@mui/material/Button';
import MyImage from './image.jpg';
import './eventrequest.css';
import Forms from "./forms.jsx";
import requestApi from '../utils/axios'; // Import the Axios utility function

function EventRequest() {
  const [isContentVisible, setIsContentVisible] = useState(true);
  const [isFormSubmitted, setIsFormSubmitted] = useState(false);

  const eventRequested = () => {
    setIsContentVisible(false);
  };

  const handleFormSubmit = async (formData) => {
    try {
      // Make a POST request using Axios
      const response = await requestApi("POST", "/submitForm", formData);
      if (response.success) {
        // If the request is successful, update the component state
        setIsContentVisible(true);
        setIsFormSubmitted(true);
      } else {
        // Handle the case where the request was not successful
        console.error('Form submission failed:', response.error);
      }
    } catch (error) {
      // Handle any unexpected errors
      console.error('Error submitting form:', error);
    }
  };

  return (
    <div className="container">
      <VerticalNavbar />
      <div className="rightcontainer">
        <TopBar />

        <div className="body">
          {isContentVisible ? (
            <div className="event-request">
              <img src={MyImage} alt="" className="event-request-image" />
              <div className="event-request-inner">
                <Button onClick={eventRequested}>
                  Create Request
                </Button>
              </div>
            </div>
          ) : (
            <div className="form-page">
              {isFormSubmitted ? (
                <p>Form submitted successfully!</p>
              ) : (
                <Forms onSubmit={handleFormSubmit} />
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default EventRequest;
