import React, {  useState } from "react";
import VerticalNavbar from '../navbar/navbar.jsx';
import TopBar from '../topbar/topbar.jsx';
import Button from '@mui/material/Button';
import MyImage from './image.jpg';
import {  useLocation, useNavigate } from 'react-router-dom';

function EventRequest() {
  const [isContentVisible, setIsContentVisible] = useState(true);
  

  const handleNavigate = (path) => {
    navigate(path);
    setSelectedField(null);
  };

  useEffect(() => {
   
    const currentPath = location.pathname.substring(1); 
    setSelectedField(currentPath || 'myevents');
  }, [location.pathname]);


  return (
    <div className="container">
      <VerticalNavbar />
      <div className="rightcontainer">
        <TopBar />

        <div className="body">
          {isContentVisible ? (
            <div className="event-request">
              <img src={MyImage} alt="My Image" className="event-request-image" />
              <div className="event-request-inner">
                <Button onClick={() => handleNavigate('/form')}>
                  Create Request
                </Button>
              </div>
            </div>
          ) : (
            <div className="category-page">
              <h3>hello</h3>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default EventRequest;
