import React, { useEffect, useState } from "react";
import VerticalNavbar from '../navbar/navbar.jsx';
import TopBar from '../topbar/topbar.jsx';
import Button from '@mui/material/Button';
import MyImage from './image.jpg';
import { useNavigate } from 'react-router-dom';

// Import the Forms component
import Forms from './forms';

function EventRequest() {
  const [isFormsVisible, setIsFormsVisible] = useState(false);
  const navigate = useNavigate();

  const handleOpenForms = () => {
    setIsFormsVisible(true);
  };

  // You can use useEffect to navigate when isFormsVisible becomes true
  useEffect(() => {
    if (isFormsVisible) {
      navigate('/eventrequest/form');
    }
  }, [isFormsVisible, navigate]);

  return (
    <div className="container">
      <VerticalNavbar />
      <div className="rightcontainer">
        <TopBar />

        <div className="body">
          {isFormsVisible ? (
            // Render the Forms component when isFormsVisible is true
            <Forms />
          ) : (
            // Render the Create Request button when isFormsVisible is false
            <div className="event-request">
              <img src={MyImage} alt="My Image" className="event-request-image" />
              <div className="event-request-inner">
                <Button onClick={handleOpenForms}>
                  Create Request
                </Button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default EventRequest;
