import React, { useEffect, useState } from "react";
import VerticalNavbar from '../navbar/navbar.jsx';
import TopBar from '../topbar/topbar.jsx';
import Button from '@mui/material/Button';
import MyImage from './image.jpg';
import { useLocation, useNavigate } from 'react-router-dom';

// Import the Forms component
import Forms from './forms.jsx';

function EventRequest() {
  const [selectedField, setSelectedField] = useState(null);
  const [isFormsVisible, setIsFormsVisible] = useState(false);
  const location = useLocation();
  const navigate = useNavigate();

  const handleNavigate = (path) => {
    navigate(path);
    setSelectedField(null);
  };

  const handleOpenForms = () => {
    setIsFormsVisible(true);
  };

  useEffect(() => {
    const currentPath = location.pathname.substring(1);
    setSelectedField(currentPath || 'myevents');
  }, [location.pathname]);

  return (
    <div className="container">
      <VerticalNavbar />
      <div className="rightcontainer">
        <TopBar />

        <div className="body">
          <div className="event-request">
            <img src={MyImage} alt="My Image" className="event-request-image" />
            <div className="event-request-inner">
              <Button onClick={handleOpenForms}>
                Create Request
              </Button>
            </div>
          </div>

          {/* Render the Forms component only when isFormsVisible is true */}
          {isFormsVisible && <Forms />}
        </div>
      </div>
    </div>
  );
}

export default EventRequest;
